 3→import type { MiddlewareHandler } from "hono";
 4→import type { AppEnv } from "./infrastructure/auth/auth.middleware.js";
 5→import type { Database } from "./infrastructure/db/index.js";
 6→
 7→// Repositories
 8→import { createDrizzleOrderRepository } from "./infrastructure/repositories/drizzle-order.repository.js";
 9→import { createDrizzleWishRepository } from "./infrastructure/repositories/drizzle-wish.repository.js";
10→import { createDrizzleBasketRepository } from "./infrastructure/repositories/drizzle-basket.repository.js";
11→import { createDrizzleUserRepository } from "./infrastructure/repositories/drizzle-user.repository.js";
12→
13→// Use Cases
14→import { createOrderUseCase } from "./application/use-cases/order/create-order.use-case.js";
15→import { listOrdersUseCase } from "./application/use-cases/order/list-orders.use-case.js";
16→import { getOrderUseCase } from "./application/use-cases/order/get-order.use-case.js";
17→import { createWishUseCase } from "./application/use-cases/wish/create-wish.use-case.js";
18→import { listUserWishesUseCase } from "./application/use-cases/wish/list-user-wishes.use-case.js";
19→import { listOrderWishesUseCase } from "./application/use-cases/wish/list-order-wishes.use-case.js";
20→import { createBasketUseCase } from "./application/use-cases/basket/create-basket.use-case.js";
21→import { listOrderBasketsUseCase } from "./application/use-cases/basket/list-order-baskets.use-case.js";
22→import { getBasketUseCase } from "./application/use-cases/basket/get-basket.use-case.js";
23→import { listUsersUseCase } from "./application/use-cases/user/list-users.use-case.js";
24→
25→// Routes
26→import { createOrderRoutes } from "./infrastructure/http/routes/order.routes.js";
27→import { createWishRoutes } from "./infrastructure/http/routes/wish.routes.js";
28→import { createBasketRoutes } from "./infrastructure/http/routes/basket.routes.js";
29→import { createUserRoutes } from "./infrastructure/http/routes/user.routes.js";
30→
31→export interface AppDependencies {
32→  db: Database;
33→  sessionMiddleware: MiddlewareHandler<AppEnv>;
34→  authRouteHandler: Hono;
35→  corsOrigins: string[];
36→}
37→
38→export function createApp(deps: AppDependencies) {
39→  const { db, sessionMiddleware, authRouteHandler, corsOrigins } = deps;
40→
41→  // 1. Repositories
42→  const orderRepo = createDrizzleOrderRepository(db);
43→  const wishRepo = createDrizzleWishRepository(db);
44→  const basketRepo = createDrizzleBasketRepository(db);
45→  const userRepo = createDrizzleUserRepository(db);
46→
47→  // 2. Use Cases
48→  const orderUseCases = {
49→    createOrder: createOrderUseCase(orderRepo),
50→    listOrders: listOrdersUseCase(orderRepo),
51→    getOrder: getOrderUseCase(orderRepo),
52→  };
53→
54→  const wishUseCases = {
55→    createWish: createWishUseCase(wishRepo, orderRepo),
56→    listUserWishes: listUserWishesUseCase(wishRepo),
57→    listOrderWishes: listOrderWishesUseCase(wishRepo),
58→  };
59→
60→  const userUseCases = {
61→    listUsers: listUsersUseCase(userRepo),
62→  };
63→
64→  const basketUseCases = {
65→    createBasket: createBasketUseCase(basketRepo),
66→    listOrderBaskets: listOrderBasketsUseCase(basketRepo),
67→    getBasket: getBasketUseCase(basketRepo),
68→  };
69→
70→  // 3. App
71→  const app = new Hono<AppEnv>();
72→
73→  app.use(
74→    "/api/*",
75→    cors({
76→      origin: corsOrigins,
77→      credentials: true,
78→    }),
79→  );
80→
81→  app.use("/api/*", sessionMiddleware);
82→
83→  app.route("/api/auth", authRouteHandler);
84→  app.route("/api/orders", createOrderRoutes(orderUseCases));
85→  app.route("/api/wishes", createWishRoutes(wishUseCases));
86→  app.route("/api/users", createUserRoutes(userUseCases));
87→  app.route("/api/baskets", createBasketRoutes(basketUseCases));
88→
89→  app.get("/", (c) => {
90→    return c.json({ status: "ok", timestamp: new Date().toISOString() });
91→  });
92→
93→  app.get("/health", (c) => {
94→    return c.json({ status: "ok", timestamp: new Date().toISOString() });
95→  });
96→
97→  return app;
98→}
