import { Link } from '@tanstack/react-router'
import { useAuthSession } from '@/features/auth/domain/auth.repository'

interface NavProps {
  onLinkClick?: () => void
}

export function Nav({ onLinkClick }: NavProps) {
  const { isAuthenticated, isAdmin } = useAuthSession()

  const linkClass =
    'text-sm font-medium transition-colors hover:text-primary text-muted-foreground'
  const activeLinkClass = 'text-sm font-medium text-primary'

  return (
    <nav className="flex flex-col gap-4 md:flex-row md:items-center md:gap-6">
      <Link
        to="/"
        onClick={onLinkClick}
        className={linkClass}
        activeProps={{ className: activeLinkClass }}
        activeOptions={{ exact: true }}
      >
        Accueil
      </Link>

      {isAuthenticated && (
        <Link
          to="/wishes"
          onClick={onLinkClick}
          className={linkClass}
          activeProps={{ className: activeLinkClass }}
        >
          Mes souhaits
        </Link>
      )}

      {isAuthenticated && (
        <Link
          to="/orders"
          onClick={onLinkClick}
          className={linkClass}
          activeProps={{ className: activeLinkClass }}
        >
          Commandes ouvertes
        </Link>
      )}

      {isAdmin && (
        <Link
          to="/admin/orders"
          onClick={onLinkClick}
          className={linkClass}
          activeProps={{ className: activeLinkClass }}
        >
          Commandes
        </Link>
      )}
    </nav>
  )
}

import { LoaderCircle } from 'lucide-react'
import type { Order } from '@/features/orders/domain/order.types'
import { OrderCardList } from './order-card-list'
import { OrderTable } from './order-table'

export function OrderListContent({
  orders,
  isPending,
  error,
}: {
  orders: Order[]
  isPending: boolean
  error: string | null
}) {
  if (error) {
    return (
      <div className="bg-destructive/10 text-destructive text-sm p-3 rounded-md">
        {error}
      </div>
    )
  }

  if (isPending) {
    return (
      <div className="flex justify-center py-12">
        <LoaderCircle className="text-muted-foreground h-6 w-6 animate-spin" />
      </div>
    )
  }

  if (orders.length === 0) {
    return <p className="text-muted-foreground">Aucune commande pour le moment.</p>
  }

  return (
    <>
      <OrderCardList orders={orders} />
