 1→import { Hono } from "hono";
 2→import type { AppEnv } from "../../auth/auth.middleware.js";
 3→import { requireAuth, requireAdmin } from "../../auth/auth.middleware.js";
 4→import {
 5→  createOrderSchema,
 6→  type CreateOrderInput,
 7→} from "../../../application/schemas/order.schema.js";
 8→import type { Order, OrderStatus } from "../../../domain/entities/order.entity.js";
 9→
10→interface OrderUseCases {
11→  listOrders: (status?: OrderStatus) => Promise<Order[]>;
12→  getOrder: (id: string) => Promise<Order | undefined>;
13→  createOrder: (input: CreateOrderInput, userId: string) => Promise<Order>;
14→}
15→
16→export function createOrderRoutes(useCases: OrderUseCases) {
17→  const routes = new Hono<AppEnv>();
18→
19→  routes.get("/", requireAuth, async (c) => {
20→    const status = c.req.query("status") as OrderStatus | undefined;
21→    const orders = await useCases.listOrders(status);
22→    return c.json(orders);
23→  });
24→
25→  routes.get("/:id", requireAuth, async (c) => {
26→    const order = await useCases.getOrder(c.req.param("id"));
27→    if (!order) {
28→      return c.json({ error: "Commande introuvable" }, 404);
29→    }
30→    return c.json(order);
31→  });
32→
33→  routes.post("/", requireAdmin, async (c) => {
34→    const body = await c.req.json();
35→    const parsed = createOrderSchema.safeParse(body);
36→    if (!parsed.success) {
37→      return c.json({ error: parsed.error.flatten() }, 400);
38→    }
39→
40→    const user = c.get("user");
41→    const order = await useCases.createOrder(parsed.data, user.id);
42→    return c.json(order, 201);
43→  });
44→
45→  return routes;
46→}
47→

 1→import type { IOrderRepository } from "../../../domain/ports/order.repository.port.js";
 2→import type { OrderStatus } from "../../../domain/entities/order.entity.js";
 3→
 4→export function listOrdersUseCase(orderRepo: IOrderRepository) {
 5→  return async (status?: OrderStatus) => {
 6→    if (status) {
 7→      return orderRepo.findByStatus(status);
 8→    }
 9→    return orderRepo.findAll();
10→  };
11→}
12→

 1→export type OrderType = "monthly" | "private_sale" | "special";
 2→export type OrderStatus = "open" | "in_progress" | "completed";
 3→
 4→export interface Order {
 5→  id: string;
 6→  type: OrderType;
 7→  targetDate: Date;
 8→  description: string | null;
 9→  status: OrderStatus;
10→  createdBy: string;
11→  createdAt: Date;
12→  updatedAt: Date;
13→}
14→
15→export interface OrderWithRelations extends Order {
16→  wishes?: Wish[];
17→  baskets?: Basket[];
18→  creator?: User;
19→}
20→
21→// Avoid circular imports — use import type
22→import type { Wish } from "./wish.entity.js";
23→import type { Basket } from "./basket.entity.js";
24→import type { User } from "./user.entity.js";
25→

 1→import { eq } from "drizzle-orm";
 2→import type { IOrderRepository } from "../../domain/ports/order.repository.port.js";
 3→import type { Order } from "../../domain/entities/order.entity.js";
 4→import type { Database } from "../db/index.js";
 5→import { order } from "../db/schema.js";
 6→
 7→export function createDrizzleOrderRepository(db: Database): IOrderRepository {
 8→  return {
 9→    async findAll() {
10→      return db.query.order.findMany({
11→        orderBy: (order, { desc }) => [desc(order.createdAt)],
12→      }) as Promise<Order[]>;
13→    },
14→
15→    async findById(id: string) {
16→      return db.query.order.findFirst({
17→        where: eq(order.id, id),
18→      }) as Promise<Order | undefined>;
19→    },
20→
21→    async findByStatus(status) {
22→      return db.query.order.findMany({
23→        where: eq(order.status, status),
24→        orderBy: (order, { desc }) => [desc(order.createdAt)],
25→      }) as Promise<Order[]>;
26→    },
27→
28→    async create(data) {
29→      const [result] = await db.insert(order).values(data).returning();
30→      return result as Order;
31→    },
32→
33→    async update(id: string, data) {
34→      const [result] = await db.update(order).set(data).where(eq(order.id, id)).returning();
35→      return result as Order | undefined;
36→    },
37→  };
38→}
39→

